"use client";

import { useRef, useState } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import Draggable from "react-draggable";
import { Resizable } from "react-resizable";

import "bootstrap/dist/css/bootstrap.min.css";
import "react-pdf/dist/Page/AnnotationLayer.css";
import "react-pdf/dist/Page/TextLayer.css";
import "@/styles/pdfviewer.css";
import { Rnd } from "react-rnd";

pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  "pdfjs-dist/build/pdf.worker.min.mjs",
  import.meta.url
).toString();

interface PDFViewerProps {
  base64Data: string | null;
}

interface Signature {
  x: number;
  y: number;
  width: number;
  height: number;
  imgSrc: string;
}

export default function PDFViewer({ base64Data }: PDFViewerProps) {
  const [numPages, setNumPages] = useState<number | null>(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState<number>(1.4); // Mặc định 140%
  const [showSignatureList, setShowSignatureList] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [selectedSignature, setSelectedSignature] = useState<string | null>(
    null
  );
  const [signaturesOnPage, setSignaturesOnPage] = useState<
    Record<number, Signature[]>
  >({});
  const [selectedSignatureIndex, setSelectedSignatureIndex] = useState<
    number | null
  >(null);
  const [hoveredSignatureIndex, setHoveredSignatureIndex] = useState<
    number | null
  >(null);
  const [isDraggingSignature, setIsDraggingSignature] = useState(false);

  const pdfContainerRef = useRef<HTMLDivElement>(null);
  const [isDragging, setIsDragging] = useState(false);

  const [signatureSize, setSignatureSize] = useState({
    width: 100,
    height: 50,
  });

  const handleMouseClick = (e: React.MouseEvent) => {
    if (!selectedSignature) return;

    // Kiểm tra ref có tồn tại không trước khi truy cập thuộc tính
    const container = pdfContainerRef.current;
    if (!container) return;

    const rect = container.getBoundingClientRect();
    if (!rect) return;

    const x = (e.clientX - rect.left + container.scrollLeft) / scale;
    const y = (e.clientY - rect.top + container.scrollTop) / scale;

    setSignaturesOnPage((prev) => {
      const updated = { ...prev };
      updated[pageNumber] = [
        ...(updated[pageNumber] || []),
        {
          x,
          y,
          width: signatureSize.width,
          height: signatureSize.height,
          imgSrc: selectedSignature,
        },
      ];
      return updated;
    });

    setSelectedSignature(null);
  };

  const signatures = [
    {
      id: 1,
      name: "Chữ ký A",
      imgSrc:
        "data:image/png;base64,",
    },
    {
      id: 2,
      name: "Chữ ký B",
      imgSrc:
        "data:image/png;base64,",
    },
  ];

  const zoomIn = () => setScale((prevScale) => prevScale + 0.1);
  const zoomOut = () =>
    setScale((prevScale) => (prevScale > 0.5 ? prevScale - 0.1 : prevScale));

  function onDocumentLoadSuccess({ numPages }: { numPages: number }) {
    setNumPages(numPages);
  }

  return (
    <div className="pdf-viewer-container">
      {/* Sidebar Thumbnail */}
      <div className="pdf-thumbnails">
        {numPages &&
          Array.from(new Array(numPages), (_, index) => (
            <div
              key={index + 1}
              className={`thumbnail ${
                pageNumber === index + 1 ? "active" : ""
              }`}
              onClick={() => setPageNumber(index + 1)}
            >
              <Document file={`data:application/pdf;base64,${base64Data}`}>
                <Page pageNumber={index + 1} width={80} />
              </Document>
              <span className="thumbnail-label">Trang {index + 1}</span>
            </div>
          ))}
      </div>

      {/* Main PDF Content */}
      <div className="pdf-content">
        <div className="toolbar">
          <button
            className="btn btn-light btn-sm"
            disabled={pageNumber <= 1}
            onClick={() => setPageNumber((prev) => Math.max(prev - 1, 1))}
          >
            ◄
          </button>
          <span>
            {pageNumber} / {numPages}
          </span>
          <button
            className="btn btn-light btn-sm"
            disabled={pageNumber >= (numPages || 1)}
            onClick={() =>
              setPageNumber((prev) => Math.min(prev + 1, numPages || 1))
            }
          >
            ►
          </button>
          <div className="zoom-container">
            <button className="btn btn-primary" onClick={zoomOut}>
              -
            </button>
            <input
              className="form-control"
              value={`${scale * 100}%`}
              readOnly
            />
            <button className="btn btn-primary" onClick={zoomIn}>
              +
            </button>
          </div>
          <div className="btn-group">
            <button
              type="button"
              className="btn btn-primary dropdown-toggle"
              data-bs-toggle="dropdown"
              aria-expanded="false"
              onClick={() => setShowSignatureList(!showSignatureList)}
            >
              🖊 Chọn chữ ký
            </button>
            {showSignatureList && (
              <ul
                className="dropdown-menu show"
                style={{ display: "block", top: "40px" }}
              >
                {signatures.map((sig) => (
                  <li
                    key={sig.id}
                    className={`signature-item ${
                      selectedSignature === sig.imgSrc ? "selected" : ""
                    }`}
                  >
                    <button
                      className="dropdown-item d-flex align-items-center"
                      onClick={() => {
                        setSelectedSignature(sig.imgSrc);
                        setIsDraggingSignature(true);
                        setShowSignatureList(false);
                      }}
                    >
                      <img
                        src={sig.imgSrc}
                        alt={sig.name}
                        className="me-2"
                        width="30"
                        height="20"
                      />
                      {sig.name}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {base64Data ? (
          <div
            className="pdf-container"
            ref={pdfContainerRef}
            onClick={handleMouseClick}
            onMouseUp={() => setIsDraggingSignature(false)}
          >
            <Document
              file={`data:application/pdf;base64,${base64Data}`}
              onLoadSuccess={onDocumentLoadSuccess}
            >
              <Page pageNumber={pageNumber} scale={scale} />
            </Document>
          </div>
        ) : (
          <p className="text-center mt-3">Chưa có file PDF để hiển thị</p>
        )}
        {signaturesOnPage[pageNumber]?.map((sig, index) => (
          <Rnd
            key={index}
            defaultPosition={{ x: sig.x, y: sig.y }}
            size={{ width: sig.width, height: sig.height }}
            bounds="parent"
            enableResizing={selectedSignatureIndex === index}
            disableDragging={
              isResizing ||
              (!isDraggingSignature && hoveredSignatureIndex !== index)
            }
            onMouseEnter={() => {
              setHoveredSignatureIndex(index);
              setSelectedSignatureIndex(index);
            }}
            onMouseLeave={() => {
              setTimeout(() => {
                if (!isDraggingSignature && !isResizing) {
                  setHoveredSignatureIndex(null);
                }
              }, 200);
            }}
            onDragStart={() => {
              if (!isResizing) setIsDraggingSignature(true);
            }}
            onDragStop={(e, d) => {
              setIsDraggingSignature(false);
              setSignaturesOnPage((prev) => {
                const updated = { ...prev };
                updated[pageNumber] = [...(updated[pageNumber] || [])];
                updated[pageNumber][index] = {
                  ...updated[pageNumber][index],
                  x: d.x / scale,
                  y: d.y / scale,
                };
                return updated;
              });
            }}
            onResizeStart={() => {
              setIsResizing(true); // Bắt đầu resize
            }}
            onResizeStop={(e, direction, ref, delta, position) => {
              setIsResizing(false); // Kết thúc resize

              setSignaturesOnPage((prev) => {
                const updated = { ...prev };
                updated[pageNumber] = [...(updated[pageNumber] || [])];
                updated[pageNumber][index] = {
                  ...updated[pageNumber][index],
                  width: ref.offsetWidth / scale, // Lấy width sau resize
                  height: ref.offsetHeight / scale, // Lấy height sau resize
                  x: position.x / scale, // Cập nhật vị trí nếu resize làm thay đổi vị trí
                  y: position.y / scale,
                };
                return updated;
              });
            }}
          >
            <div
              className={`signature-box ${
                selectedSignatureIndex === index ? "selected" : ""
              }`}
              style={{
                position: "relative",
                background: "white",
                border: "1px solid black",
                cursor: "pointer",
              }}
            >
              {/* Icon Remove (Góc phải trên) */}
              {selectedSignatureIndex === index && (
                <button
                  onClick={() => {
                    setSignaturesOnPage((prev) => {
                      const updated = { ...prev };
                      updated[pageNumber] = (updated[pageNumber] || []).filter(
                        (_, i) => i !== index
                      );
                      return updated;
                    });
                  }}
                  style={{
                    position: "absolute",
                    top: "-10px",
                    right: "-10px",
                    background: "red",
                    color: "white",
                    border: "none",
                    borderRadius: "50%",
                    width: "20px",
                    height: "20px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    cursor: "pointer",
                  }}
                >
                  ✖
                </button>
              )}

              <img
                src={sig.imgSrc}
                alt="Chữ ký"
                style={{ width: "100%", height: "100%" }}
              />

              {/* Icon Resize (Góc phải dưới) */}
              {selectedSignatureIndex === index && (
                <div
                  style={{
                    position: "absolute",
                    bottom: "-14px",
                    right: "-18px",
                    width: "15px",
                    height: "15px",
                    background: "gray",
                    cursor: "nwse-resize",
                  }}
                  onMouseDown={(e) => {
                    e.stopPropagation(); // Ngăn chặn sự kiện drag khi resize
                  }}
                />
              )}
            </div>
          </Rnd>
        ))}
      </div>
      {/* Log vị trí chữ ký */}
      <pre className="signature-log">
        {JSON.stringify(signaturesOnPage, null, 2)}
      </pre>
    </div>
  );
}
